import { getPhotos, getPhoto, getPostPhotos } from '../util/photo_api_util';

export const RECEIVE_PHOTOS = "RECEIVE_PHOTOS";
export const RECEIVE_PHOTO = "RECEIVE_PHOTO";
export const RECIEVE_POST_PHOTOS = "RECIEVE_POST_PHOTOS";

export const reeivePhotos = photos => ({
    type: RECIEVE_PHOTOS,
    photos
});

export const receivePostPhotos = photos => ({
    type: RECEIVE_POST_PHOTOS,
    photos
});

export const receivePhoto = photo => ({
    type: RECEIVE_PHOTO,
    photo
});

export const fetchPhotos = () => dispatch => (
    getPhotos()
    .then(photos => dispatch(receivePhotos(photos)))
    .catch(err => console.log(err))
);

export const fetchPhoto = id => dispatch => (
    getPhoto(id)
    .then(photo => dispatch(receivePhoto(photo)))
);

export const fetchPostPhotos = id => dispatch => (
    getPostPhotos(id)
        .then(photos => dispatch(receivePostPhotos(photos)))
        .catch(err => console.log(err))
);

deletePhoto = id => {
    axios.delete("/api/photos/" + id).then(() => {
      this.setState({
        photos: this.state.photos.filter(photo => photo._id !== id)
      });
    });
  };
