import React from 'react';
import './post.css';

class PostItem extends React.Component {
    constructor(props) {
        super(props)

        this.handleClick = this.handleClick.bind(this);
        this.handleButton = this.handleButton.bind(this);
    }

    handleClick() {
        this.props.history.push(`/users/${this.props.post.user}` )
    }

    handleButton(e) {
        e.preventDefault();
        this.props.removePost(this.props.post._id);
        this.props.history.go();
    }

    formatDate(date) {
        let options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' }
        let localtime = date.prototype.toLocaleDateString("en-US", options);
        return localtime;
    }

    render() {
             const post = this.props.post;
             const user = this.props.users[this.props.post.user];
////debugger
        if (post && user) {
            return (
                <li>
                    <div className="post-item">
                        <div><img className="post-item-image" src= {post.file}/></div>
                        <div className="post-item-text">{post.text}</div>
                        <div className="post-item-username"onClick={this.handleClick}>
                            {user.username}
                        </div>
                        <div className="post-date">{this.formatDate(post.time)}</div>
                        {(this.props.currentUser.id === user._id) ? 
                            <button onClick={this.handleButton}>Delete</button> : <div></div>
                        }
                    </div>
                </li>
            )
        } else {
            // ////debugger
            return null;
        }
    }
}

export default PostItem;
