import React from 'react';
import PostItem from '../posts/post_item';
import PostCompose from '../posts/post_compose';
import './profile.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowCircleLeft } from "@fortawesome/free-solid-svg-icons";
import CommentsList from '../comments/comments_list';
import LikesDislikes from '../likesdislikes/likes_dislikes';
import Moment from "react-moment";

class Profile extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: []
        }

        this.renderAsAnotherUser = this.renderAsAnotherUser.bind(this);
        this.renderCurrentUser = this.renderCurrentUser.bind(this);
    }

    componentWillMount() {
//debugger
        this.props.fetchUserPosts(this.props.user);
        this.props.fetchUsers();
        this.props.fetchPhotos();
    }

    componentWillReceiveProps(newState) {
////debugger
        this.setState({ posts: newState.posts });
    }

    capitalize(string) {
        let firstChar = string[0].toUpperCase();
        let rest = string.slice(1);

        return firstChar + rest;
    }

    renderCurrentUser() {
////debugger
        const list = Object.values(this.props.posts[1]).map((post) => {
            if (post.file) {
                return (
                    <div className="posts">
                        <li className="post-item-li">
                            <div className="post-item" key={post._id}>
                                <img className="post-item-image" src= {post.file} />
                                <div className="post-item-text">
                                    {post.text}
                                </div>
                            <div className="post-item-footer">
                        <button className="post-item-footer-button" onClick={() => {
                            return(
                            this.props.removePost(post._id)
                            .then(() => this.props.history.go())                        
                            )
                        } } 

                        >Delete</button></div></div>
                            <div className="post-item-posted">
                                Posted by <span className="post-item-username" onClick={this.handleClick}>{user.username}</span>
                            </div>
                            <Moment format="MMM DD, YYYY [at] h:mmA" className="post-date" >{dateStr}</Moment>
                            <br/>
                            <LikesDislikes postId={post._id} userId={user._id} currentUserId={this.props.currentUser.id}/>
                            {/* <div className="post-date">{localtime}</div> */}
                            {(this.props.currentUser.id === user._id) ? 
                                <button className="post-item-footer-button" onClick={this.handleButton}>Delete</button> : <div></div>
                            }
                        </li>
                    </div>
                )
            } else if (!post.file) {
                return (
                    <div className="posts">
                        <li className="post-item-li">
                            <div className="post-item" key={post._id}>
                                <div className="post-item-text">
                                    {post.text}
                                </div>
                            <div className="post-item-footer">
                        <button className="post-item-footer-button" onClick={() => {
                            return(
                            this.props.removePost(post._id)
                            .then(() => this.props.history.go())                        
                            )
                        } } 

                        >Delete</button></div></div>

                        </li>
                    </div>
                )
            }
        });

        return (
            <div>
                <div className="user-profile">
                <h2>Hello, {this.capitalize(this.props.currentUser.username)}</h2>
                <PostCompose currentUser={this.props.currentUser} newPost={this.props.newPost} composePost={this.props.composePost} history={this.props.history} />
                </div>
                <ul className="profile-list">
                    {list}
                </ul>
            </div>
        )        
    }

    renderAsAnotherUser() {
        if (this.props.users) {
            const list = Object.values(this.props.posts[1]).map((post) => {
////debugger
                if (post.file) {
                    return(
                        <li className="post-item-li"> 
                            <div className="post-item" key={post._id}>
                                <img className="post-item-image" src= {post.file} />
                                <div className="post-item-text">
                                    {post.text}
                                </div>
                            </div>
                        </li>  
                    )
                } else if (!post.file) {
                    return(
                        <li className="post-item-li"> 
                            <div className="post-item" key={post._id}>
                                <div className="post-item-text">
                                    {post.text}
                                </div>
                            </div>
                        </li>  
                    )
                }
            });

        return (
            <div className="another-class">
                <h2>All of 's Posts</h2>
                <ul className="posts">
                    {list}
                </ul>
            </div>
        )
    }
}

    render() {
        if (!this.state.user && (this.state.posts.length === 0)) {
            return (<div className="profile-main">
                <div className="profile-top-section"/>
                <div className="profile-loading">Loading...</div>
                </div>
            )
        } else {
            return (
                <div className="profile-main">
                    <div className="profile-top-section"/>
                    {/* <div className="profile-main-body"> */}
                        <button className="profile-button" onClick={() => this.props.history.push('/posts')}><FontAwesomeIcon className="font-awesome-back" icon={faArrowCircleLeft} /></button>
                        {this.props.currentUser.id === this.props.user ? 
                        this.renderCurrentUser() : this.renderAsAnotherUser()}
                    {/* </div> */}
                </div>
            )
        }
    }
}

export default Profile;
